import React, {useEffect} from "react";
import {Link, useParams, useNavigate, useLocation} from 'react-router-dom'
import {useDispatch, useSelector} from 'react-redux'
import {Row, Col, ListGroup, Image, Form, Button, Card} from 'react-bootstrap'

import Message from '../components/Message'
import {addToCart} from '../actions/cartActions'

function CartScreen({}){
    const navigate = useNavigate();
    const location = useLocation()
    const {id} = useParams()
    const productId = id
    const qty = new URLSearchParams(location.search).get('qty') ? Number(new URLSearchParams(location.search).get('qty').split('=')[0]):1

    const dispatch = useDispatch()

    const cart = useSelector(state => state.cart)
    const { cartItems} = cart
    console.log('cartItems:', cartItems)
    useEffect(()=>{
        if(productId){
            dispatch(addToCart(productId,qty))
        }
    }, [dispatch, productId, qty])

    const removeFromCartHandler = () => {
        console.log('remove:', id)
    }
    return(
        <Row>
            <Col md={8}>
                <h1>Shopping Cart</h1>
                {cartItems.length === 0? (<Message variant='info'>Your Cart is empty <Link to='/'>Go Back</Link></Message>)
                : (<ListGroup variant="flush">
                    {cartItems.map(item=>(
                        <ListGroup.Item key={item.product}>
                            <Row>
                                <Col md={2}>
                                    <Image src={item.image} fluid rounded/>
                                </Col>

                                <Col md={3}>
                                    <Link to={`/product/${item.product}`}>{item.name}</Link>
                                </Col>

                                <Col md={2}>
                                    &{item.price}
                                </Col>

                                <Col md={3}>
                                    <Form.Control as="select" value={item.qty} 
                                         onChange={(e) => dispatch(addToCart(item.product, e.target.value))} >
                                        {
                                            [...Array(item.countInStock).keys()].map((x)=>(
                                            <option key={x+1} value={x+1} >
                                                {x+1}
                                            </option> )) } 
                                    </Form.Control>
                                </Col>

                                <Col md={1}>
                                    <Button type='button' variant="light"
                                        onClick={()=> removeFromCartHandler(item.product)}>
                                        <i className="fas fa-trash"/>

                                    </Button>
                                </Col>
                                <Col md={4}>
                                    <ListGroup variant="flush">
                                            <ListGroup.Item>
                                                <h2>SubTotal</h2>
                                            </ListGroup.Item>
                                    </ListGroup>
                                </Col>
                            </Row>
                        </ListGroup.Item>

                    ))}
                </ListGroup>)}
            </Col>

            <Col md={4}>
            </Col>
        </Row>
    )
}

export default CartScreen
